catalog_script_client.script:
function onSubmit() {

	var environments = '';
	if ( g_form.getValue('devtest') == 'true') { environments = environments + 'devtest,' ;}
	if ( g_form.getValue('dev') == 'true') { environments = environments + 'dev,';}
	if ( g_form.getValue('uat') == 'true') { environments = environments + 'uat,';}
	if ( g_form.getValue('prod') == 'true') { environments = environments + 'prod,';}

	alert('running query for enviroments ' + environments);

	// GlideAjax to get tower instance details
	var ga = new GlideAjax('get_tower_instances'); 
	ga.addParam('sysparm_name','getTowerInstances'); 
	ga.addParam('sysparm_environments', environments);
	ga.getXML(ParseTowerInstances);
}

// Process the response from the server and create a list of ID's
function ParseTowerInstances(response) {  
	var answer = response.responseXML.documentElement.getAttribute("answer"); 
	alert(answer);
	var instances = JSON.parse(answer);
	var instances_ids = [];
	for(var i = 0 ; i < instances.length ; i ++){
		instances_ids.push(instances[i].tower_instance_sys_id);
	}
	g_form.setValue('tower_instance', instances_ids);
	//g_form.setValue('tower_deployment_instances', answer);
}
