catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   var team = g_form.getReference('ansible_tower_team_lookup',update_form);
}

function update_form(team){
	// alert('Setting ORG values!');
	g_form.setValue('team_name',team.name);
	g_form.setValue('team_description',team.description);
	g_form.setValue('team_id',team.id);
	g_form.setValue('distinct_environment_groups',team.distinct_environment_groups);
	g_form.setValue('devtest',team.devtest);
	g_form.setValue('dev',team.dev);
	g_form.setValue('uat',team.uat);
	g_form.setValue('prod',team.prod);
	// create token for child MVRS
	sessionStorage.setItem("team_list",""+team.id);
	var request_type = g_form.getValue('request_type');

	if (request_type == 'create') {
		g_form.setVisible('ansible_tower_team', true); 
		g_form.setReadOnly('ansible_tower_team', false); 
		g_form.clearValue('ansible_tower_team');
	}

	// GlideAjax to get the team roles
	var ga = new GlideAjax('get_team_info'); 

	if (request_type == 'delete') {
		g_form.setVisible('ansible_tower_team', true); 
		g_form.setReadOnly('ansible_tower_team', true); 
		g_form.setVisible('ansible_tower_team_role_mrvs', true);
		g_form.setReadOnly('ansible_tower_team_role_mrvs', true);
		ga.addParam('sysparm_name','getTeamRoles'); 
		ga.addParam('sysparm_team', team.sys_id); 
		ga.getXML(getTeamsRolesParse);
		//alert('sent the request');
	}	
	if (request_type == 'modify') {
		g_form.setVisible('ansible_tower_team', true); 
		g_form.setReadOnly('ansible_tower_team', false);
		g_form.setVisible('ansible_tower_team_role_mrvs', true);
		g_form.setReadOnly('ansible_tower_team_role_mrvs', false);
		ga.addParam('sysparm_name','getTeamRoles'); 
		ga.addParam('sysparm_team', team.sys_id); 
		ga.getXML(getTeamRolesParse);
		//alert('sent the request');
	}
}
function getTeamRolesParse(response) {  
	var answer = response.responseXML.documentElement.getAttribute("answer"); 
	//alert(answer);
	g_form.setValue('ansible_tower_team_role_mrvs', answer);
}
