catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	org = g_form.getValue('ansible_tower_organisation_lookup');
	var ga = new GlideAjax('get_teams'); 
	ga.addParam('sysparm_name','get_teams'); 
	ga.addParam('sysparm_org', org); 
	ga.getXML(get_teams_parse);  /* Call HelloWorld.helloWorld() with the parameter sysparm_user_name set to 'Bob' 
      and use the callback function HelloWorldParse() to return the result when ready */

	// the callback function for returning the result from the server-side code
	function get_teams_parse(response) {  
		var answer = response.responseXML.documentElement.getAttribute("answer"); 
    //alert(answer);
	g_form.setValue('ansible_tower_team_mrvs', answer);
	}

}
