catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   var tower_organisation = g_form.getReference('tower_organisation',update_form);
}

function update_form(tower_organisation){
   var onboarded_to_live_environments = tower_organisation.onboarded_to_live_environments;
   if ( onboarded_to_live_environments == 'false' ) {
		g_form.setValue('devtest', 'true');
		g_form.setValue('dev', 'false');
		g_form.setValue('uat', 'false');
		g_form.setValue('prod', 'false');
		g_form.setReadOnly('dev', true);
		g_form.setReadOnly('uat', true);
		g_form.setReadOnly('prod', true);
		sessionStorage.setItem("permitted_environments", 'r_devtest');
   } else {
		g_form.setValue('devtest', 'true');
		g_form.setValue('dev', 'true');
		g_form.setValue('uat', 'true');
		g_form.setValue('prod', 'true');
		g_form.setReadOnly('devtest', false);
		g_form.setReadOnly('dev', false);
		g_form.setReadOnly('uat', false);
		g_form.setReadOnly('prod', false);
		sessionStorage.setItem("permitted_environments", 'r_devtest,r_dev,r_uat,r_prod');
   }
}
