catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
   //Type appropriate comment here, and begin script below
   var team_roles_required = g_form.getValue('trigger_team_roles');
   if (team_roles_required == 'Yes') {
		g_form.setVisible('ansible_tower_team_role_mrvs', true);
		var teamMRVS = JSON.parse(g_form.getValue('ansible_tower_team_mrvs'));
		var teams = [];
		for (var i = 0; i < teamMRVS.length; i++) {
			teams.push(teamMRVS[i].team_id);//add all totals from the MRVS to 1 total
		}
		g_form.setValue('team_list', teams);
		sessionStorage.setItem("team_list", ""+teams);
		sessionStorage.setItem("permitted_environments", ""+g_form.getValue('deploy_to_environments'));
   } 
   if (team_roles_required == 'No') {
		g_form.setVisible('ansible_tower_team_role_mrvs', false);
   }  
}
