catalog_script_client.script:
function onLoad() {
    // Populate the team list
	g_form.setVisible('role_id', false);
	var team_list = sessionStorage.getItem("team_list");
    var team_list_array = team_list.split(",");
	for (var i = 0; i < team_list_array.length; i++) {
		g_form.addOption('team', team_list_array[i], team_list_array[i] );
	}

	// Update the options allowed
	//g_form.setReadOnly('r_devtest', true);
	//g_form.setReadOnly('r_dev', true);
	//g_form.setReadOnly('r_uat', true);
	//g_form.setReadOnly('r_prod', true);
	//var permitted_environments = sessionStorage.getItem("permitted_environments");
	//var permitted_environments_array = permitted_environments.split(",");
	//for (var e = 0; e < permitted_environments_array.length; e++) {
	//	g_form.setReadOnly(permitted_environments_array[e], false);
	//	g_form.setValue(permitted_environments_array[e], 'true');
	//}

	// Set the role_id
	var role = g_form.getValue('role');
	var team = g_form.getValue('team');
	var role_id = team + '__' + role.replace(/ /g, '_'); 
	g_form.setVisible('role_id', true);
	g_form.setValue('role_id', role_id);
	g_form.setReadOnly('role_id', true);
}
