catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
   var request_type = g_form.getValue('request_type');
   if (request_type == 'create') {
		g_form.setVisible('ansible_tower_use_case', true);
		g_form.setReadOnly('ansible_tower_use_case', false);
		g_form.clearValue('use_case_name');
		g_form.clearValue('use_case_technical_owner');
		g_form.clearValue('use_case_description');
		g_form.clearValue('use_case_documentation_url');
		g_form.setVisible('ansible_tower_use_case_lookup', false);
   } 
   if (request_type == 'modify') {
		g_form.setVisible('ansible_tower_use_case_lookup', true);
		g_form.setReadOnly('ansible_tower_use_case', true);
   } 
	if (request_type == 'delete') {
		g_form.setVisible('ansible_tower_use_case_lookup', true);
		g_form.setReadOnly('ansible_tower_use_case', true);
   } 
   //Type appropriate comment here, and begin script below

}
