catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	//Type appropriate comment here, and begin script below
   var teams_required = g_form.getValue('trigger_teams');
   if (teams_required == 'Yes'){
		g_form.setVisible('trigger_team_roles', true);
		g_form.setVisible('ansible_tower_team_mrvs', true);

		var org = g_form.getValue('tower_organisation');
		var ga = new GlideAjax('get_team_info'); 
		ga.addParam('sysparm_name','getTeams'); 
		ga.addParam('sysparm_org', org); 
		ga.getXML(getTeamsParse);
		//alert('sent the request');
   } 
   if (teams_required == 'No') {
		g_form.setVisible('trigger_team_roles', false);
		g_form.setVisible('ansible_tower_team_mrvs', false);
   }  
}

function getTeamsParse(response) {  
	var answer = response.responseXML.documentElement.getAttribute("answer"); 
	//alert(answer);
	g_form.setValue('ansible_tower_team_mrvs', answer);
}
