sys_script_include.script:
var get_team_info = Class.create();
get_team_info.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


	getTeams: function(){
		var arr = [];
		var org = this.getParameter('sysparm_org');
		var gr = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_teams');
		gr.addQuery('organisation',org);
		gr.query();
		gs.info('searching for ' + org + ' ' + gr.getRowCount() + 'records found');
		while(gr.next()){
			var obj = {};
			obj["team_name"] = gr.getValue('name');
			obj["team_id"] = gr.getValue('id');
			obj["team_description"] = gr.getValue('description');
			obj["devtest"] = gr.getDisplayValue('devtest');
			obj["dev"] = gr.getDisplayValue('dev');
			obj["uat"] = gr.getDisplayValue('uat');
			obj["prod"] = gr.getDisplayValue('prod');
			obj["distinct_environment_groups"] = gr.getValue('distinct_environment_groups');
			arr.push(obj);
		}
		gs.info('Sending to client form ' + JSON.stringify(arr));
		return JSON.stringify(arr);
	},
		getTeamRoles: function(){
		var arr = [];
		var team = this.getParameter('sysparm_team');
		var gr = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_team_roles');
		gr.addQuery('team',team);
		gr.query();
		gs.info('searching for ' + team + ' ' + gr.getRowCount() + 'records found');
		while(gr.next()){
			var obj = {};
			obj["team"] = gr.getDisplayValue('team');
			obj["role"] = gr.getDisplayValue('role');
			obj["role_id"] = gr.getValue('id');
			obj["r_devtest"] = gr.getDisplayValue('devtest');
			obj["r_dev"] = gr.getDisplayValue('dev');
			obj["r_uat"] = gr.getDisplayValue('uat');
			obj["r_prod"] = gr.getDisplayValue('prod');
			arr.push(obj);
		}
		gs.info('Sending to client form ' + JSON.stringify(arr));
		return JSON.stringify(arr);
	},
    type: 'get_team_info'
});
