sys_script_include.script:
var get_tower_instances = Class.create();
get_tower_instances.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getTowerInstances: function(){
		var arr = [];
		var envString = this.getParameter('sysparm_environments');
		var environments = envString.split(',');
		gs.info('searching for tower instances in ' + environments );
		var gr = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_instances');
		gr.addQuery('tower_instance_environment.alias', 'IN', environments);
		gr.query();
		gs.info('searching for tower instances in ' + environments + ' ' + gr.getRowCount() + ' records found');
		while(gr.next()){
			var obj = {};
			obj["tower_instance_environment"] = gr.getDisplayValue('tower_instance_environment');
			obj["tower_instance_location"] = gr.getDisplayValue('tower_instance_location');
			obj["tower_instance_url"] = gr.getValue('tower_instance_url');
			obj["tower_instance"] = gr.getValue('tower_instance');
			obj["tower_instance_sys_id"] = gr.getValue('sys_id');
			arr.push(obj);
		}
		gs.info('Sending to client form ' + JSON.stringify(arr));
		return JSON.stringify(arr);
	},
	type: 'get_tower_instances'
});
