// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
//   answer = ifScript();
//
//   function ifScript() {
//      if (condition is true) {
//         return 'yes';
//      }
//      return 'no';
//   }

var job_status = workflow.scratchpad.tower_job_status;
var job_id = workflow.scratchpad.tower_job_id;
answer = ifScript();
//
gs.info("Tower Job ID:" + job_id + "Tower Rechecking Job Status:" + job_status );
function ifScript() {
  if ( (job_status == 'pending') || ( job_status == 'running' ) ) {
    // add details to the work notes of request
    current.work_notes = workflow.scratchpad.tower_job_reponse;
    current.update();
    return 'yes';
    }
    return 'no';
}

