// Set the variable 'answer' to true or false to indicate if the condition has been met or not.
checkAnisbleTowerNotifications();
function checkAnisbleTowerNotifications() {
	var gr = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_notifcations');
	//  Search for the SCTASK in the notications table with status successful or failed
	gr.addQuery('sctask', workflow.scratchpad.task_number);
	var qc = gr.addQuery('tower_job_status', 'successful');
	qc.addOrCondition('tower_job_status', 'failed');  
	gr.query();
	if(gr.hasNext()){
		answer = true;
	}
	else{
		answer = false;
	}
}

