	//  Search for the SCTASK in the notications table with status successful or failed
	gr.addQuery('sctask', workflow.scratchpad.task_number);
	var qc = gr.addQuery('tower_job_status', 'successful');
	qc.addOrCondition('tower_job_status', 'failed');  
	gr.query();
	if(gr.hasNext()){
		answer = true;
	}
	else{
		answer = false;
	}
}

