// if statement on ASAP as per maintenace window or specifc time
if (( current.variables.ssc_sn_asap == 'true'  )) {
   var maint_sched_id = current.variables.ssc_sn_server.maintenance_schedule;
   var schedule = new GlideSchedule(maint_sched_id);
   var whenNext = schedule.whenNext();
   gs.info("CR_SCHED Next maintenance window starts in: " + whenNext + " milliseconds");
   gs.info("CR_SCHED That is: " + (whenNext/(1000*60*60)) + " hours");
   var gdt = new GlideDateTime();
   var cr_start = gdt.add(whenNext);
   gs.info("CR_SCHED cr start is: " + cr_start );
   var cr_end = cr_start.addSeconds(7200);
   gs.info("CR_SCHED cr end is: " + cr_end );
   workflow.scratchpad.cr_start= cr_start ; 
   workflow.scratchpad.cr_end = cr_end ; 
} else if (( current.variables.ssc_sn_asap == 'false'  )) {
   workflow.scratchpad.cr_start = current.variables.ssc_sn_start_window ; 
   workflow.scratchpad.cr_end = current.variables.ssc_sn_end_window ; 
}

