 try {
 var r = new sn_ws.RESTMessageV2('x_ibmss_gts_oar_at.Ansible Tower ', 'JOB_TEMPLATE LAUNCH BY ID (POST)');
 r.setStringParameterNoEscape('tower_project_purpose', current.variables.tower_project_purpose );
 r.setStringParameterNoEscape('tower_project_description', current.variables.tower_project_description);
 r.setStringParameterNoEscape('tower_inventory_purpose', current.variables.tower_inventory_purpose );
 r.setStringParameterNoEscape('tower_inventory_path', current.variables.tower_inventory_path );
 r.setStringParameterNoEscape('tower_team_purpose', current.variables.tower_team_purpose );
 r.setStringParameterNoEscape('tower_project_git_instance', current.variables.tower_project_git_instance );
 r.setStringParameterNoEscape('tower_project_branch', current.variables.tower_project_branch );
 r.setStringParameterNoEscape('tower_inventory_description', current.variables.tower_inventory_description );
 r.setStringParameterNoEscape('tower_project', current.variables.tower_project );
 r.setStringParameterNoEscape('tower_team_description', current.variables.tower_team_description );
 r.setStringParameterNoEscape('tower_environment', current.variables.tower_environment );
 r.setStringParameterNoEscape('tower_project_repository', current.variables.tower_project_repository );
 r.setStringParameterNoEscape('ssc_sn_appcode', 'OAR');
 r.setStringParameterNoEscape('tower_job_template_id', '184');
 r.setStringParameterNoEscape('ssc_sn_org', 'SSC');
 r.setStringParameterNoEscape('tower_instance', 'ansibletower-lab.it.statestr.com');

//override authentication profile 
//authentication type ='basic'/ 'oauth2'
//r.setAuthentication(authentication type, profile name);

//set a MID server name if one wants to run the message on MID
//r.setMIDServer('MY_MID_SERVER');

//if the message is configured to communicate through ECC queue, either
//by setting a MID server or calling executeAsync, one needs to set skip_sensor
//to true. Otherwise, one may get an intermittent error that the response body is null
//r.setEccParameter('skip_sensor', true);

 var response = r.execute();
 var responseBody = response.getBody();
 var httpStatus = response.getStatusCode();
 gs.info("Response: " + responseBody);
 gs.info("HTTP Status: " + httpStatus);
 // convert json respone into javacript object
 var responseObj = JSON.parse(responseBody);
 // log the job ID
 gs.info("Tower_Job_ID:" + responseObj.job );
 gs.info("Tower_Job_URL:" + responseObj.url ); 
 gs.info("Tower_Job_Status:" + responseObj.status ); 
 // export tower job details in vars
 workflow.scratchpad.tower_job_id = responseObj.job;
 workflow.scratchpad.tower_job_url = responseObj.url;
 workflow.scratchpad.tower_job_status = responseObj.status ;
 // add details to the work notes of request
 current.work_notes = responseBody ;
 current.update();
}
catch(ex) {
 var message = ex.getMessage();
}

