function ifScript() {
	
	current.work_notes = 'Checking the status of change request';
	var grGet = new GlideRecord('change_request');
	grGet.addQuery('sys_id',workflow.scratchpad.sys_id);
	grGet.query();
	if(grGet.next()){
		if(grGet.state == 304){
			current.work_log = 'Change Request is approved.';
			return 'yes';
		}
			
	}
	current.work_notes = 'Change Request is not approved. Hence cancelling the request';
	return 'yes';
}

