   function ifScript() {
		var req = new sn_ws.RESTMessageV2('x_ibmss_dcf.Server Build Application', 'LOGIN_REQUEST');
		var response = req.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
        var responseObj = JSON.parse(responseBody);
	    workflow.scratchpad.requestToken = 'APIC-Cookie='+responseObj.imdata[0].aaaLogin.attributes.token;
		if (httpStatus != 200) {
			  gs.error('Unable to obtain the token ' + responseBody);
			 return 'no';
		}
	    gs.info('Token Obtained');
		return 'yes';
   }

