if(current.variables.next_window == 'true'){
	
	var sTime = new Date();
	var eTime = new Date();
	sTime.setDate(sTime.getDate() + 1);
	eTime.setDate(eTime.getDate() + 1);
	//var noOfDays = 6 - currentTime.getDay();
	//currentTime.setDate(currentTime.getDate()+noOfDays);
    sTime.setHours(18,0,0);
	eTime.setHours(18,10,0);
	//currentTime.setHours(18,0,0,0);
	
    workflow.scratchpad.cr_start= moment(sTime).utc().format(outputFmt); 
	workflow.scratchpad.cr_end= moment(eTime).utc().format(outputFmt);
	current.work_notes ="Request Start time is: " + workflow.scratchpad.cr_start +'(GMT)\n' +"Expected End time is: " + workflow.scratchpad.cr_end+'(GMT)';
	
	var requestStartTime = moment(sTime).utc().valueOf();
	gs.info('requestStartTime ' + requestStartTime);
	workflow.scratchpad.requestStartTime=requestStartTime;
	
} else{
	var dateTime=current.variables.time_zone;
	
	if(dateTime != null && dateTime != ''){
		try{
			var date =moment(dateTime,fmt).valueOf();
			var start = moment(dateTime,fmt).format(outputFmt);
			var end = moment(dateTime,fmt).add(10,'minutes').format(outputFmt);
			workflow.scratchpad.cr_start = start; 
			workflow.scratchpad.cr_end = end;
			workflow.scratchpad.requestStartTime=date;
		}
		catch(ex) {
			gs.error("Failed to issue tower job:" + ex );
		}
	}
	current.work_notes = "Request Start Time is: " +  workflow.scratchpad.cr_start + '(GMT)\n' +  'Expected End Time is: ' +workflow.scratchpad.cr_end+'(GMT)';
	

}

