   function ifScript() {
	    var token = workflow.scratchpad.requestToken;
	    gs.info('Token ' + token);
	    var req = new sn_ws.RESTMessageV2('x_ibmss_dcf.Server Build Application', 'GET_POLICY_GROUPS');
		req.setStringParameterNoEscape('requestToken', token);
		var response = req.execute();
        var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();
	    if(httpStatus != 200){
			gs.error('Unable retrieve the existing policy groups');
			return 'no';
		}
        var responseObj = JSON.parse(responseBody);
		for(i =0;i<responseObj.imdata.length;i++){
			try{
				var p = responseObj.imdata[i].infraAccBndlGrp.attributes.name.toString();
		    	oldPolicyGroups.push(p);
			}catch(e){
				gs.info('Error fetching existing policies' + e);
			}
			
		}
	    req = new sn_ws.RESTMessageV2('x_ibmss_dcf.Server Build Application', 'GET_INTERFACE_PROFILES');
		req.setStringParameterNoEscape('requestToken', token);
		response = req.execute();
        responseBody = response.getBody();
		httpStatus = response.getStatusCode();
	    if(httpStatus != 200){
			gs.error('Unable retrieve the existing interface profiles');
			return 'no';
		}
        responseObj = JSON.parse(responseBody);
	
		for(i =0;i<responseObj.imdata.length;i++){
			try{
				var profile = responseObj.imdata[i].infraAccPortP.attributes.name.toString();
		    	oldInterfaceProfile.push(profile);
			}catch(e){
				gs.info('Error fetching existing profiles' + e);
			}
			
		}
	    req = new sn_ws.RESTMessageV2('x_ibmss_dcf.Server Build Application', 'GET_FABRIC_PODS');
		req.setStringParameterNoEscape('requestToken', token);
		response = req.execute();
        responseBody = response.getBody();
		httpStatus = response.getStatusCode();
	    if(httpStatus != 200){
			gs.error('Unable retrieve the fabric pods');
			return 'no';
		}
        responseObj = JSON.parse(responseBody);
	  
		for(i =0;i<responseObj.imdata.length;i++){
			try{
				var pod = responseObj.imdata[i].fabricPod.attributes.dn.toString();
				var reqForNode = new sn_ws.RESTMessageV2('x_ibmss_dcf.Server Build Application', 'GET_FABRIC_NODES');
		    	reqForNode.setStringParameterNoEscape('requestToken', token);
				reqForNode.setStringParameterNoEscape('podName', pod);
				var responseForNode = reqForNode.execute();
				var responseBodyForNode = responseForNode.getBody();
				var httpStatusForNode = responseForNode.getStatusCode();
				var responseObjForNode = JSON.parse(responseBodyForNode);
				
				 
				for(var j =0;j<responseObjForNode.imdata.length;j++){
					try{
						var nodeName = responseObjForNode.imdata[j].fabricNode.attributes.dn.toString();
						var switchName = responseObjForNode.imdata[j].fabricNode.attributes.name.toString();
						var id = responseObjForNode.imdata[j].fabricNode.attributes.id.toString();
						allNodes[switchName]=nodeName;
						switchIds[switchName] = id;
						switches.push(switchName);
						var reqForPorts = new sn_ws.RESTMessageV2('x_ibmss_dcf.Server Build Application', 'GET_FREE_PORTS');
						reqForPorts.setStringParameterNoEscape('requestToken', token);
						reqForPorts.setStringParameterNoEscape('nodeName', nodeName);
						var responseForPorts = reqForPorts.execute();
						var responseBodyForPorts = responseForPorts.getBody();
						var httpStatusForPorts = responseForPorts.getStatusCode();
						var responseObjForPorts = JSON.parse(responseBodyForPorts);
						
						for(var k=0;k<responseObjForPorts.imdata.length;k++){
							try{
								var portNumber = responseObjForPorts.imdata[k].l1PhysIf.attributes.id.toString();
								portNumber = portNumber.split("/")[1];
								var myInterfaceChild = responseObjForPorts.imdata[k].l1PhysIf.children;
								var myDesc = responseObjForPorts.imdata[k].l1PhysIf.attributes.descr.toString();
								if (("" ===myDesc  || myDesc.toLowerCase().indexOf("not in use") != -1) && (myInterfaceChild[0].ethpmPhysIf.attributes.operSt.toString().toLowerCase()==="down")){
									freePort.push(portNumber);
									
								}
								
							}catch(error){
							//	gs.info('responseObjForPorts error ' + error);
							}
							 
						}
						
						allPorts[nodeName] = freePort;
						freePort = [];
					}catch(e){
					//	gs.info('Error fetching node names' + e);
					}
				}
			}catch(e){
				gs.info('Error fetching pod details' + e);
			}
			
		}
	 
	   switches.sort();
	   for(var index=0;index < switches.length;index++){

			var value = switches[index];
			var name = value.substring(0,value.length-1);
			var pair = value.substring(value.length-1);
			var asc = pair.charCodeAt(0);
			var ascPair;
            if( asc < 65 || asc > 90){
				continue;}
			if(asc % 2 === 0)
				ascPair = asc - 1;
			else
				ascPair= asc + 1;
			var pairName = name+String.fromCharCode(ascPair);
			for(var v in switches){
				if(switches[v] === pairName){
					switchPairs[value]= pairName;
				}
			}
	   }
	for(var key in switchPairs){
	
		if(key in allNodes && switchPairs[key] in allNodes){
			var nodeOnePorts = allPorts[allNodes[key]];
			var nodeTwoPorts = allPorts[allNodes[switchPairs[key]]];
			for(var portIndex = 0; portIndex < nodeOnePorts.length; portIndex++){
				if(nodeTwoPorts.indexOf(nodeOnePorts[portIndex]) !== -1){
					switchIdFull = switchIds[key];
					switchId = switchIds[key].substring(1);
					idPair =  switchIds[key] +'-'+ switchIds[switchPairs[key]];
					workflow.scratchpad.node_id1 = switchIds[key];
					workflow.scratchpad.node_id2 = switchIds[switchPairs[key]];
					selectedPort = nodeOnePorts[portIndex];
					policyGroupName = os +'_'+ nic + '_'+switchId+'_1_'+selectedPort+'_10G_LACP_INTPLGR';
					interfaceProfileName = nic + '_'+switchId+'_1_'+selectedPort+'_'+os+'_INTPFL';
					var pgExist = false;
					var ipExist = false;
					switchNameOne = key;
					switchNameTwo = switchPairs[key];
					var isPGReserved = false;
					var isIPReserved = false;
					for(var opg in oldPolicyGroups){
						if(oldPolicyGroups[opg] === policyGroupName){
							pgExist = true;
						}
					}
					for(var oip in oldInterfaceProfile){
						if(oldInterfaceProfile[oip] === interfaceProfileName){
							ipExist = true;
						}
					}
					var grGet = new GlideRecord('x_ibmss_dcf_reserved_ports');
					grGet.addQuery('policy_name',policyGroupName);
					grGet.query();
					if(grGet.next()){
						isPGReserved = true;
					}
					grGet = new GlideRecord('x_ibmss_dcf_reserved_ports');
					grGet.addQuery('interface_name',interfaceProfileName);
					grGet.query();
					if(grGet.next()){
						isIPReserved = true;
					}
					if(pgExist == false && ipExist == false && isPGReserved == false && isIPReserved == false){
						portAllocated = true;
						break;
					}
				}
			}
			
		}
		if(portAllocated){
			break;
		}
	}
	   
	workflow.scratchpad.switchId = switchId;
	workflow.scratchpad.interfaceProfileName = interfaceProfileName;
	workflow.scratchpad.policyGroupName = policyGroupName;
	workflow.scratchpad.selectedPort = selectedPort;
	workflow.scratchpad.idPair = idPair;
	workflow.scratchpad.switchNameOne = switchNameOne;
	workflow.scratchpad.switchNameTwo = switchNameTwo;
	workflow.scratchpad.switchIdFull=switchIdFull;
	current.work_notes='Reserved Details \n\tPolicy Group Name: ' + policyGroupName +'\n\tInterface Profile Name: '+interfaceProfileName;
	if(portAllocated){
		var gr = new GlideRecord('x_ibmss_dcf_reserved_ports');
		gr.initialize(); 
		gr.request_nbr = workflow.scratchpad.requestNumber;
		gr.policy_name = policyGroupName; 
		gr.interface_name = interfaceProfileName; 
		gr.insert();
		return 'yes';
	}
	else{
		return 'no';
	}
  }

