// Check and see the Tower organisation record exsists and if not create it
if (current.variables.org_action == 'create'){
	var gr = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_organisations');
	// Check and see if the org already exsists 
	gr.addQuery('name',current.variables.organisation_name);
    gr.query();
    if (!gr.next()) {
		// Create a new Incident record and populate the fields with the values below
		gr.initialize();
		gr.active = true;
		gr.group_setup = current.variables.organisation_group_setup;
		gr.admins = current.variables.organisation_admins;
		gr.members = current.variables.organisation_members;
		gr.business_owner = current.variables.organisation_business_owner;
		gr.technical_owner = current.variables.organisation_technical_owner;
		gr.description = current.variables.organisation_description;
		gr.name = current.variables.organisation_name;
		workflow.scratchpad.tower_organisation = gr.insert();
    }
} else {
	// Organisation already exsists, just save that record as a workflow scatchpad variable 
	workflow.scratchpad.tower_organisation = current.variables.tower_organisation;
}

