var gr = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_organisations');
switch(current.variables.request_type.toString()) {	
	case 'modify' :
	//Find record and update
	gr.addQuery('name',current.variables.organisation_name);
    gr.query();
    while (gr.next()) {
		gr.active = true;
		gr.group_setup = current.variables.organisation_group_setup;
		gr.admins = current.variables.organisation_admins;
		gr.members = current.variables.organisation_members;
		gr.business_owner = current.variables.organisation_business_owner;
		gr.technical_owner = current.variables.organisation_technical_owner;
        gr.description = current.variables.organisation_description;
		gr.update();
		// workflow.scratchpad.tower_organisation = gr.update();
    }
	break;
		
	case 'delete' :
	//Find records and mark not active
	gr.addQuery('name',current.variables.organisation_name);
    gr.query();
    while (gr.next()) {
		gr.active = false;
		gr.update();
		// workflow.scratchpad.tower_organisation = gr.update();
    }
	break;
    
	default :
    // Catch all, noaction
}

