var jsonString = current.variables.tower_team;
var Teams = JSON.parse(jsonString);
var TeamCount = Teams.length;
if ( TeamCount > 0 ) {
// gs.info(TeamCount + ' Teams found');

for(var i=0;i<Teams.length;i++){
	var gr = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_teams');
	// gs.info('Running through team '+ Teams[i].team_name );
    switch(current.variables.request_type.toString()) {
		case "create" :
		// Create a new Incident record and populate the fields with the values below
			gr.initialize();
			gr.active = true;
			gr.description = Teams[i].team_description;
			gr.name = Teams[i].team_name;
			gr.deploy_to_all_environments = Teams[i].deploy_to_all_environments_or_specific;
			gr.deploy_to_devtest = Teams[i].deploy_team_to_devtest;
			gr.deploy_to_dev = Teams[i].deploy_team_to_dev;
			gr.deploy_to_uat = Teams[i].deploy_team_to_uat;
			gr.deploy_to_prod = Teams[i].deploy_team_to_prod;
			gr.organisation = workflow.scratchpad.tower_organisation;
			gr.insert();
			break;
		
		case "modify" :
			//Find records and update
			gr.addQuery('name',Teams[i].team_name);
			gr.query();
			while (gr.next()) {
				gr.active = true;
				gr.description = Teams[i].team_description;
				gr.name = Teams[i].team_name;
				gr.deploy_to_all_environments = Teams[i].deploy_to_all_environments_or_specific;
				gr.deploy_to_devtest = Teams[i].deploy_team_to_devtest;
				gr.deploy_to_dev = Teams[i].deploy_team_to_dev;
				gr.deploy_to_uat = Teams[i].deploy_team_to_uat;
				gr.deploy_to_prod = Teams[i].current.variables.deploy_team_to_prod;
				gr.organisation = workflow.scratchpad.tower_organisation;
				gr.update();
			}
			break;
		
		case "delete" :
		//Find records and mark not active
			gr.addQuery('name',Teams[i].team_name);
			gr.query();
			while (gr.next()) {
				gr.actvie = false;
				gr.update();
			}
			break;
    
		default :
        // Catch up didn't find a action
			gs.info(current.number + ': No request type action for Update Tower Teams Table');
			break;
	}
}
}else {
	gs.info('Didnt find any teams');
}

