gs.info(current.number + ' looking for a ' + workflow.scratchpad.tower_unified_job_template_type + ' named ' + workflow.scratchpad.tower_unified_job_template_name );
var rec = new GlideRecord('x_ibmss_gts_oar_at_ansible_tower_unified_job_templates');
rec.addQuery('tower_unified_job_template_name', workflow.scratchpad.tower_unified_job_template_name);
rec.addQuery('tower_unified_job_template_type', workflow.scratchpad.tower_unified_job_template_type);

// TODO add support for looking up the instance to determine the instance
// rec.addQuery('tower_instance.region', workflow.scratchpad.tower_region);
// rec.addQuery('tower_instance.environment', workflow.scratchpad.tower_environment);
rec.query();
while (rec.next()) { 
  // Proces that record
  workflow.scratchpad.tower_instance = rec.tower_instance.tower_instance;
  workflow.scratchpad.tower_instance_url = rec.tower_instance.tower_instance_url;
  gs.info(current.number + ': Using Ansible Tower Instance ' + rec.tower_instance.tower_instance);
  workflow.scratchpad.tower_unified_job_template_id = rec.tower_unified_job_template_id;
  gs.info(current.number + ': Using Ansible Tower Unified Job ID ' + rec.tower_unified_job_template_id);
}

