// Build payload, start with extra vars
var payload = {};
payload.extra_vars = {};
payload.extra_vars.ritm = current.number;

// Org details
var tower_organisation = {};
if (current.variables.tower_organisation != undefined) { 
	payload.extra_vars.tower_organisation = current.variables.tower_organisation;

} else {
	tower_organisation.name = current.variables.organisation_name;
    tower_organisation.description = current.variables.organisation_description;
    tower_organisation.admins = current.variables.organisation_admins;
    tower_organisation.members= current.variables.organisation_members;
	payload.extra_vars.tower_organisation = tower_organisation;
}


// Team details
if (current.variables.tower_team != undefined) {
	gs.info(current.number + ': Adding teams to the payload ' + current.variables.tower_team );
	var tower_teams = JSON.parse(current.variables.tower_team);
	payload.extra_vars.tower_teams = tower_teams;
}

// add limit if needed
// add inventory if needed


// Export payload to scratchpad
workflow.scratchpad.payload = payload;

gs.info(current.number + ': payload is ' + workflow.scratchpad.payload );

