try {
	workflow.scratchpad.ecc_id_correlator = gs.generateGUID();
	var restMessage = new sn_ws.RESTMessageV2('x_ibmss_gts_oar_at.Ansible Tower ', 'UNIFIED_JOB_TEMPLATE LAUNCH');

	
	endpoint = workflow.scratchpad.tower_instance_url + '/api/v2/' + workflow.scratchpad.tower_unified_job_template_type + '/' + workflow.scratchpad.tower_unified_job_template_id + '/launch/';
	
	restMessage.setEndpoint(endpoint);
	
	
	// Build the json object using workflow.scratchpad.payload
	requestBody = new global.JSON().encode(workflow.scratchpad.payload);
    restMessage.setRequestBody(requestBody);
	
	//override authentication profile
	//authentication type ='basic'/ 'oauth2'
	//r.setAuthentication(authentication type, profile name);
	
	//set a MID server name if one wants to run the message on MID
	//r.setMIDServer('MY_MID_SERVER');
	
	//if the message is configured to communicate through ECC queue, either
	//by setting a MID server or calling executeAsync, one needs to set skip_sensor
	//to true. Otherwise, one may get an intermittent error that the response body is null
	restMessage.setEccParameter('skip_sensor', true);
	
	// Set the correlator ID
	restMessage.setEccCorrelator(workflow.scratchpad.ecc_id_correlator);
	
	// Debug 
	gs.info(current.number + ': Lanunching job with correlator id ' + workflow.scratchpad.ecc_id_correlator);
	gs.info(current.number + ': Request Body ' + JSON.stringify(requestBody));

	// Run the job async
	restMessage.executeAsync();
}
catch(ex) {
	var message = ex.getMessage();
	gs.error("Failed to issue tower job:" + message );
}

