 payload.extra_vars.ssc_sn_owner = "SSC-CA-ETS-OS-UNIX";
 payload.extra_vars.ssc_sn_platform = "Linux";
 payload.extra_vars.ssc_sn_region = "EMEA";
 payload.extra_vars.ssc_sn_requester_email = ""+current.opened_by.email;
 payload.extra_vars.ssc_sn_requester_id =  ""+current.opened_by.user_name;
 payload.extra_vars.ssc_sn_ritm = ""+current.number;
 if (( current.variables.ssc_sn_function == 'APP' )){
    //  app install
 payload.extra_vars.ssc_sn_software_product = ""+current.variables.ssc_sn_software_choice_1.u_package_name;
 payload.extra_vars.ssc_sn_software_product_version = ""+current.variables.ssc_sn_software_choice_1.u_version;
 } else {
    //  db install
 payload.extra_vars.ssc_sn_software_product = ""+current.variables.ssc_sn_database_choice.u_package_name;
 payload.extra_vars.ssc_sn_software_product_version = ""+current.variables.ssc_sn_database_choice.u_version;
 }
 payload.extra_vars.ssc_sn_tech_plan_id = "TP12345";
 payload.extra_vars.ssc_sn_vm_cpu = ""+current.variables.ssc_sn_vm_cpu;
 payload.extra_vars.ssc_sn_vm_ram = ""+current.variables.ssc_sn_vm_ram;
 payload.extra_vars.ssc_sn_vm_size = ""+current.variables.ssc_sn_vm_size;
 payload.extra_vars.ssc_vm_instance_count = 1;
 var json = new global.JSON();
 var json_payload = json.encode(payload);
 gs.info("json_payload: " + global.JSON.stringify(json_payload) );
 r.setRequestBody(json_payload);

	
	//override authentication profile
	//authentication type ='basic'/ 'oauth2'
	//r.setAuthentication(authentication type, profile name);
	
	//set a MID server name if one wants to run the message on MID
	//r.setMIDServer('MY_MID_SERVER');
	
	//if the message is configured to communicate through ECC queue, either
	//by setting a MID server or calling executeAsync, one needs to set skip_sensor
	//to true. Otherwise, one may get an intermittent error that the response body is null
	//r.setEccParameter('skip_sensor', true);
	
	var response = r.execute();
	var responseBody = response.getBody();
	var httpStatus = response.getStatusCode();
	gs.info("Response: " + responseBody);
	gs.info("HTTP Status: " + httpStatus);
	// convert json respone into javacript object
	var responseObj = JSON.parse(responseBody);
	// log the job ID
	gs.info("Tower_Job_ID:" + responseObj.workflow_job );
	gs.info("Tower_Job_URL:" + responseObj.url );
	gs.info("Tower_Job_Status:" + responseObj.status );
	// export tower job details in vars
	workflow.scratchpad.tower_job_id = responseObj.workflow_job;
	workflow.scratchpad.tower_job_url = responseObj.url;
	workflow.scratchpad.tower_job_status = responseObj.status ;
	// add details to the work notes of request
	current.work_notes = responseBody ;
	current.update();
}
catch(ex) {
	var message = ex.getMessage();
	gs.error("Failed to issue tower job:" + message );
}

